import React, { useState } from 'react';
import logo from './logo.svg';
import './App.css';
import { loginUser } from './loginService.js';
import { getFavoriteMovies, getAllMovies} from './movieService'
import axios, { all } from 'axios';


function App() {
  const [username, setUsername] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  let allMovies = [];

  const handleLogin = async () => {
    //loginUser(username);
    getFavoriteMovies(username);
    setIsLoggedIn(true);
    getAllMovies2().then((response) => {
      console.log(response);
    })
    console.log(allMovies)

    showMovies();
  };

  const showMovies = async () => {
    allMovies.map((movie, index) => (
      <p key={index}>{movie.Title}</p>
    ))
  };

  const getAllMovies2 = async () => {
    return new Promise((resolve, reject) => {
      axios
        .get('http://localhost:3000/getAllMovies')
        .then(response => {
          resolve(response.data);
        })
        .catch(error => {
          reject(error);
        });
    });
  }


  // const handleFavoriteMovies = async () => {
  //   getFavoriteMovies(username);
  // };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setUsername('');
  };

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        {isLoggedIn ? (
          <>
            <p>Welcome, {username}!</p>
            <button onClick={handleLogout}>Logout</button>
            {/* Add the view for favorite movies here */}
            <p>Available Movies:</p>
            {allMovies.map((movie, index) => (
              <p key={index}>{movie.Title}</p>
            ))}
            <p>Your favorite movies:</p>
            {/* Render the favorite movies */}
          </>
        ) : (
          <>
            <input
              type="text"
              placeholder="Username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
            <br />
            <br />
            <button onClick={handleLogin}>Login</button>
          </>
        )}
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}

export default App;
