import React, { useState } from 'react';
import logo from './logo.svg';
import './App.css';
import { loginUser } from './loginService.js';
import { getFavoriteMovies } from './movieService'
import axios from 'axios';


function App() {
  const [username, setUsername] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleLogin = async () => {
    loginUser(username);
    getFavoriteMovies(username);
  };

  // const handleFavoriteMovies = async () => {
  //   getFavoriteMovies(username);
  // };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setUsername('');
  };

  return (
    React.createElement('div', { className: 'App' },
      React.createElement('header', { className: 'App-header' },
        React.createElement('img', { src: logo, className: 'App-logo', alt: 'logo' }),
        isLoggedIn ?
          React.createElement(React.Fragment, null,
            React.createElement('p', null, 'Welcome, ', username, '!'),
            React.createElement('button', { onClick: handleLogout }, 'Logout')
          ) :
          React.createElement(React.Fragment, null,
            React.createElement('input', {
              type: 'text',
              placeholder: 'Username',
              value: username,
              onChange: (e) => setUsername(e.target.value)
            }),
            React.createElement('br'),
            React.createElement('br'),
            React.createElement('button', { onClick: handleLogin }, 'Login')
          ),
        React.createElement('a', {
          className: 'App-link',
          href: 'https://reactjs.org',
          target: '_blank',
          rel: 'noopener noreferrer'
        }, 'Learn React')
      )
    )
  );
}

export default App;
